import React, { Component } from "react";
import { Button, ButtonGroup, Container, Table } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';


class AcronymList extends Component {

    constructor(props) {
        super(props);
        this.state = { acronyms: [], isLoading: true };
    }

    async componentDidMount() {
        const response = await fetch('/api/acronym/all')
        const body = await response.json();
        console.log(body);
        this.setState({ acronyms: body, isLoading: false });
    }

    async remove(id) {

        await fetch('/api/acronym/all/'+id, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedGroups = [...this.state.acronyms].filter(i => i.id !== id);
            this.setState({ acronyms: updatedGroups });
        });

    }

    buttonDelete = (cell, row, rowIndex, formatExtraData) => {
        return (
            <Button size="sm" variant="danger" onClick={() => this.remove(row.id)}>Delete</Button>
            )

    }

  

    render() {

        const { acronyms, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading... </p>;
        }

       //Using normal Table from React Bootstrap
       /* const AcronymList = acronyms.map(acronym => {
            
            return (<tr key={acronym.id}>
                <td>{acronym.acronym}</td>
                <td>{acronym.full_term}</td>
                <td>{acronym.remark || ''}</td>

                <td>
                    <ButtonGroup>
                        <Button size="sm" variant="danger" onClick={() => this.remove(acronym.id)}>Delete</Button>
                    </ButtonGroup>
                </td>

            </tr>);

        });


        return (
            <div>
                <Container fluid>
                    <Button variant="success" className="float-right">Add Acronym</Button>
                    <h2>Acronym List</h2>
                    <Table>
                        <thead>
                            <tr>
                                <th width="20%">Acronym</th>
                                <th width="20%">Full Term</th>
                                <th>Remark</th>
                                <th width="10%">Actions</th>

                            </tr>
                        </thead>
                        <tbody>
                            {AcronymList}
                        </tbody>
                    </Table>
                </Container>
            </div>
        );
         */

        //Using React Bootstrap Table 2
        const data = acronyms;

        const columns = [{
            dataField: 'id',
            text: 'acronym id',
            hidden: true
        }, {
            dataField: 'acronym',
            text: 'Acronym'
        }, {
            dataField: 'full_term',
            text: 'Full Term'
        }, {
            dataField: 'remark',
            text: 'Remarks'
        }, {
                dataField: 'actions',
                text: 'Actions',
                formatter: this.buttonDelete
            }];

        return (
            <div>
                <Container fluid>
                    <Button variant="success" className="float-right">Add Acronym</Button>
                    <h2>Acronym List</h2>
                    <BootstrapTable keyField='id' data={data} columns={ columns }/>
                </Container>
            </div>
        )

    }

}

export default AcronymList;
