import React, { Component } from "react";
import { Button, Container, Form, Row, Col } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';
import cellEditFactory from 'react-bootstrap-table2-editor';
import paginationFactory from 'react-bootstrap-table2-paginator';

class AcronymList extends Component {

    constructor(props) {
        super(props);
        this.state = { acronyms: [], isLoading: true, isAdding: false, page : 1,sizePerPage : 10 };
    }

    async componentDidMount() {
        const response = await fetch('/api/acronym/all?offset=' + (this.state.page - 1) + '&limit=' + this.state.sizePerPage);
        const body = await response.json();
        console.log(body);
        const responseSize = await fetch('/api/acronym/all/count');
        const sizeResult = await responseSize.json();
        console.log(sizeResult);
        this.setState({ acronyms: body, isLoading: false, totalSize: sizeResult, rowCount: sizeResult });
    }

    async remove(id) {

        await fetch('/api/acronym/all/'+id, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedAcronyms = [...this.state.acronyms].filter(i => i.id !== id);
            this.setState({ acronyms: updatedAcronyms });
        });

    }

    async addNewAcronym() {

        const newAcronym = {
            acronym: this.acronymInput.value,
            full_term: this.full_termInput.value
        };

        await fetch('api/acronym/new', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newAcronym),
        });

        const response = await fetch('/api/acronym/all')
        const body = await response.json();
        console.log(body);
        this.setState({ acronyms: body});
    }

    async updateAcronyms(offset,sizePerPage) {
        const response = await fetch('/api/acronym/all?offset=' + offset + '&limit=' + sizePerPage);
        const body = await response.json();
        console.log(body);
        this.setState({ acronyms: body });
    }

    async findAcronym(filterVal) {
        const response = await fetch('/api/acronym/all/' + filterVal +
            '?offset=' + 0 + '&limit=' + this.state.sizePerPage);
        const body = await response.json();
        console.log(body);
        const responseSize = await fetch('/api/acronym/all/' + filterVal + '/count');
        const sizeResult = await responseSize.json();
        this.setState({ acronyms: body, totalSize: sizeResult, page: 1 });
    }

    handleDataChange = ({ dataSize }) => {
        console.log("reaches data change");
        this.setState({ rowCount: dataSize });
    }

    buttonDelete = (cell, row, rowIndex, formatExtraData) => {
        return (
            <Button size="sm" variant="danger" onClick={() => this.remove(row.id)}>Delete</Button>
            )
    }  

    showAddAcronym = () => {
        return (
            <div className="AddAcronymForm">
                <Form>
                    <Row className="RowMargin">
                        <Col>
                            <Form.Label>Acronym</Form.Label>
                            <Form.Control placeholder="Acronym" ref={input => this.acronymInput = input}/>
                        </Col>
                        <Col>
                            <Form.Label>Full Term</Form.Label>
                            <Form.Control placeholder="Full Term" ref={input => this.full_termInput = input} />
                        </Col>
                    </Row>
                    <Row className="RowMargin">
                        <Col>
                            <Form.Label>Remarks/Comments</Form.Label>
                            <Form.Control as="textarea" placeholder="Remark" ref={input => this.remarkInput = input}/>
                        </Col>
                    </Row>
                        <Button variant="danger" className="float-right" onClick={() => { this.setState({ isAdding: false }) }}>Cancel</Button>
                        <Button variant="success" className="float-right" onClick={() => {
                        this.setState({ isAdding: false });
                        console.log(this.remarkInput.value);
                        this.addNewAcronym()
                        }}>Submit</Button>
                </Form>
            </div>
        );
    }

    handleTableChange = (type, { page, sizePerPage , filters}) => {
        //console.log("Page : " + page);
        //console.log("Size per Page : " + sizePerPage);
        console.log("Type : " + type);

        const offset = (page - 1) * sizePerPage;

        if (type === 'pagination' || type === 'cellEdit') {
            setTimeout(() => {
                        this.updateAcronyms(offset, sizePerPage);
                        this.setState({ page: page, sizePerPage: sizePerPage });
            }, 1500);
        }

        if (type === 'filter') {
            let valid = true;
            for (const dataField in filters) {
                console.log("Datafield : " + dataField);
                const { filterVal } = filters[dataField];
                console.log("filterVal : " + filterVal);
                if (dataField === 'acronym') {
                    this.findAcronym(filterVal);
                    valid = false;
                }
            }

            if (valid) {
                this.updateAcronyms(offset, sizePerPage);
                this.setState({ page: page, sizePerPage: sizePerPage });
            }
        }

    }

    render() {

        const { acronyms, isLoading } = this.state;
        const { sizePerPage, page, totalSize } = this.state;


        if (isLoading) {
            return <p>Loading... </p>;
        }

        const data = acronyms;
        const columns = [{
            dataField: 'id',
            text: 'acronym id',
            hidden: true
        }, {
                dataField: 'acronym',
                text: 'Acronym',
                filter: textFilter() 
        }, {
            dataField: 'full_term',
            text: 'Full Term',
            filter: textFilter()
        }, {
            dataField: 'remark',
            text: 'Remarks'
        }, {
                dataField: 'actions',
                text: 'Actions',
                formatter: this.buttonDelete
            }];

        const DataTable = ({ data, page, sizePerPage, onTableChange, totalSize, handleDataChange }) => (
            <div>
                <Container fluid>
                    <Button variant="success" className="float-right" onClick={() => { this.setState({ isAdding: true }) }}>Add Acronym</Button>
                    <h2>Acronym List</h2>

                    {this.state.isAdding ? this.showAddAcronym() : null}

                    <BootstrapTable
                        remote
                        striped
                        keyField="id"
                        data={data}
                        columns={columns}
                        pagination={paginationFactory({ page, sizePerPage, totalSize })}
                        cellEdit={cellEdit}
                        filter={filterFactory()}

                        onDataSizeChange={handleDataChange}
                        onTableChange={onTableChange}                     

                    />
                    
                </Container>
            </div>
        )

        const cellEdit = cellEditFactory({
            mode: 'dbclick',
            beforeSaveCell: (oldValue, newValue, row, column) => {
                console.log("Old Value : " + oldValue);
                console.log("New Value : " + newValue);
                console.log("row/elemnt ID : " + row.id);
                console.log("column : " + column.dataField);
                var updatedAcronym = {
                    acronym: row.acronym,
                    full_term: row.full_term,
                    remark: row.remark,
                    id: row.id
                }
                switch (column.dataField) {
                    case 'acronym':
                        updatedAcronym = {
                            acronym: newValue,
                            full_term: row.full_term,
                            remark: row.remark,
                            id: row.id
                        };
                        break;
                    case 'full_term':
                        updatedAcronym = {
                            acronym: row.acronym,
                            full_term: newValue,
                            remark: row.remark,
                            id: row.id
                        };
                        break;
                    case 'remark':
                        updatedAcronym = {
                            acronym: row.acronym,
                            full_term: row.full_term,
                            remark: newValue,
                            id: row.id
                        };
                        break;
                }

                fetch('api/acronym/all/' + row.id, {
                    method: 'PUT',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(updatedAcronym),
                });

            }
        });

        return (
            <div>
                <h5>Row Count:<span className="badge">{this.state.rowCount}</span></h5>
                <DataTable
                    data={data}
                    page={page}
                    sizePerPage={sizePerPage}
                    totalSize={totalSize}
                    onTableChange={this.handleTableChange}

                    onDataSizeChange={this.handleDataChange}
                />
            </div>
        )

    }

}

export default AcronymList;




