import React, { Component } from "react";
import { Button, Container, Form, Row, Col } from 'react-bootstrap';
import BootstrapTable from 'react-bootstrap-table-next';
import ToolkitProvider, { Search } from 'react-bootstrap-table2-toolkit';
import cellEditFactory from 'react-bootstrap-table2-editor';
import paginationFactory, { PaginationProvider, PaginationListStandalone } from 'react-bootstrap-table2-paginator';



class AcronymList extends Component {

    constructor(props) {
        super(props);
        this.state = { acronyms: [], isLoading: true, isAdding: false };
    }

    async componentDidMount() {
        this.state = {
            page: 0,
            sizePerPage: 10
        }
        const response = await fetch('/api/acronym/all?offset=' + this.state.page + '&limit=' + this.state.sizePerPage);
        const body = await response.json();
        console.log(body);
        this.setState({ acronyms: body, isLoading: false });
    }

    async remove(id) {

        await fetch('/api/acronym/all/'+id, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedAcronyms = [...this.state.acronyms].filter(i => i.id !== id);
            this.setState({ acronyms: updatedAcronyms });
        });

    }

    async addNewAcronym() {

        const newAcronym = {
            acronym: this.acronymInput.value,
            full_term: this.full_termInput.value
        };

        await fetch('api/acronym/new', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newAcronym),
        });

        const response = await fetch('/api/acronym/all')
        const body = await response.json();
        console.log(body);
        this.setState({ acronyms: body});
    }

    buttonDelete = (cell, row, rowIndex, formatExtraData) => {
        return (
            <Button size="sm" variant="danger" onClick={() => this.remove(row.id)}>Delete</Button>
            )
    }  

    handleTableChange = () => {
            console.log("Page : " + this.state.page);
            console.log("Size per Page : " + this.state.sizePerPage);        
    }


    showAddAcronym = () => {
        return (
            <div className="AddAcronymForm">
                <Form>
                    <Row className="RowMargin">
                        <Col>
                            <Form.Label>Acronym</Form.Label>
                            <Form.Control placeholder="Acronym" ref={input => this.acronymInput = input}/>
                        </Col>
                        <Col>
                            <Form.Label>Full Term</Form.Label>
                            <Form.Control placeholder="Full Term" ref={input => this.full_termInput = input} />
                        </Col>
                    </Row>
                    <Row className="RowMargin">
                        <Col>
                            <Form.Label>Remarks/Comments</Form.Label>
                            <Form.Control as="textarea" placeholder="Remark" ref={input => this.remarkInput = input}/>
                        </Col>
                    </Row>
                        <Button variant="danger" className="float-right" onClick={() => { this.setState({ isAdding: false }) }}>Cancel</Button>
                        <Button variant="success" className="float-right" onClick={() => {
                        this.setState({ isAdding: false });
                        console.log(this.remarkInput.value);
                        this.addNewAcronym()
                        }}>Submit</Button>
                </Form>
            </div>
        );
    }



    render() {

        const { acronyms, isLoading } = this.state;
        const { sizePerPage, page } = this.state;

        if (isLoading) {
            return <p>Loading... </p>;
        }

        const data = acronyms;
        const columns = [{
            dataField: 'id',
            text: 'acronym id', 
            hidden: true
        }, {
            dataField: 'acronym',
            text: 'Acronym'
        }, {
            dataField: 'full_term',
            text: 'Full Term'
        }, {
            dataField: 'remark',
            text: 'Remarks'
        }, {
                dataField: 'actions',
                text: 'Actions',
                formatter: this.buttonDelete
            }];

        const cellEdit = cellEditFactory({
            mode: 'dbclick',
            beforeSaveCell: (oldValue, newValue, row, column) => {
                console.log("Old Value : " + oldValue);
                console.log("New Value : " + newValue);
                console.log("row/elemnt ID : " + row.id);
                console.log("column : " + column.dataField);
                var updatedAcronym = {
                    acronym: row.acronym,
                    full_term: row.full_term,
                    remark: row.remark,
                    id: row.id
                }
                switch (column.dataField) {
                    case 'acronym':
                        updatedAcronym = {
                            acronym: newValue,
                            full_term: row.full_term,
                            remark: row.remark,
                            id: row.id
                        };
                        break;
                    case 'full_term':
                        updatedAcronym = {
                            acronym: row.acronym,
                            full_term: newValue,
                            remark: row.remark,
                            id: row.id
                        };
                        break;
                    case 'remark':
                        updatedAcronym = {
                            acronym: row.acronym,
                            full_term: row.full_term,
                            remark: newValue,
                            id: row.id
                        };
                        break;
                }

                fetch('api/acronym/all/' + row.id, {
                    method: 'PUT',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(updatedAcronym),
                });

            }
        }); 


        const RemotePagination = ({ data, page, sizePerPage, onTableChange, totalSize }) => (
            <div>
                <Container fluid>
                    <Button variant="success" className="float-right" onClick={() => { this.setState({ isAdding: true }) }}>Add Acronym</Button>
                    <h2>Acronym List</h2>

                    {this.state.isAdding ? this.showAddAcronym() : null}
                    
                    <BootstrapTable
                        remote
                        striped
                        keyField="id"
                        data={data}
                        columns={columns}
                        pagination={paginationFactory({ page, sizePerPage, totalSize, onTableChange })}
                    />
                       
                    
                </Container>
            </div>
        )

        return (
            <div>
                <RemotePagination
                    data={data}
                    page={page}
                    sizePerPage={sizePerPage}
                    totalSize={30}
                    onTableChange={this.handleTableChange}
                />
            </div>
        )

    }

}

export default AcronymList;
