import React, { Component } from "react";


class DepartmentList extends Component {

    constructor(props) {
        super(props);
        this.state = { departments: [], isLoading: true};
        //this.remove = this.remove.bind(this);
    }

    async componentDidMount() {
        const response = await fetch('/api/departments/all')
        const body = await response.json();
        console.log(body);
        this.setState({ departments: body, isLoading: false });
    }

    render() {

        const { departments, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading... </p>;
        }

        const DepartmentList = departments.map(department => {

            return (<tr key={department.department_ID}>
                <td>{department.department_Name}</td>
                <td>{department.manager_ID || ''}</td>
                <td>{department.location_ID || ''}</td>
            </tr>);

        });

        return (
            <div>
                <AppNavbar />                    
                    <h3>Department List</h3>
                    <table>
                            <tr>
                                <th width="20%">Name</th>
                                <th width="20%">Manager ID</th>
                                <th width="10%">Location ID</th>
                            </tr>
                        <tr>
                            {DepartmentList}
                        </tr>
                    </table>
            </div>
        );

    }

}

export default DepartmentList;
