import React, { Component } from "react";


class DepartmentList extends Component {

    constructor(props) {
        super(props);
        this.state = { departments: [], isLoading: true};
        //this.remove = this.remove.bind(this);
    }

    componentDidMount() {
        this.setState({ isLoading: true });

        fetch('api/departments/all')
            .then(response => response.json())
            .then(data => this.setState({ departments : data, isLoading: false }));
    }

    render() {

        const { departments, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading... </p>;
        }

        const DepartmentList = departments.map(department => {
            return (<tr key={department.department_ID}>
                    <td>{department.department_Name}</td>
                    <td>{department.manager_ID || ''}</td>
                    <td>{department.location_ID || ''}</td>
                </tr>);

        })

    }

}

export default DepartmentList;
